
public class ProductInSeries {
	
	static String array="";
	
	public static void main(String[] args) {
		long tmp=0;
		long tmp1;
		for (int i = 0; i < array.length()-12; i++) {
			int a=Integer.parseInt(""+array.charAt(i));
			int b=Integer.parseInt(""+array.charAt(i+1));
			int c=Integer.parseInt(""+array.charAt(i+2));
			int d=Integer.parseInt(""+array.charAt(i+3));
			int e=Integer.parseInt(""+array.charAt(i+4));
			int f=Integer.parseInt(""+array.charAt(i+5));
			int g=Integer.parseInt(""+array.charAt(i+6));
			int h=Integer.parseInt(""+array.charAt(i+7));
			int i1=Integer.parseInt(""+array.charAt(i+8));
			int j=Integer.parseInt(""+array.charAt(i+9));
			int k=Integer.parseInt(""+array.charAt(i+10));
			int l=Integer.parseInt(""+array.charAt(i+11));
			int m=Integer.parseInt(""+array.charAt(i+12));
			tmp1=a*b*c*d*e*f*g*h*i1*j*k*l*m;
			if(tmp<tmp1){
				System.out.println(tmp1);
				tmp=tmp1;
			}
		}
		System.out.println(tmp);
	}

}
